from collections import Counter

string_in = ''
string = ""
for l in string_in:
    if l.isalpha():
        l = l.upper()
        string += l
n = list(string)
b = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
list = []
for i in b:
    list.append(i)
d = Counter(n)
sorted_tuple = sorted(d.items(), key=lambda x: x[1])
d = dict(sorted_tuple)
l = len(string)
t = {}
for key, value in d.items():
    value = round(value * 100 / l, 2)
    value = str(value) + " %"
    t[key] = value
print("length of string (only letters) is: ", len(string))
print(t)
r = {}
m = {'Z': 0.05, 'Q': 0.10, 'J': 0.15, 'X': 0.15, 'K': 0.77, 'V': 0.98, 'B': 1.49, 'P': 1.93, 'Y': 1.97, 'G': 2.02, 'F': 2.23, 'W': 2.36, 'M': 2.41, 'U': 2.76, 'C': 2.78, 'L': 4.03, 'D': 4.35, 'R': 5.99, 'H': 6.09, 'S': 6.33, 'N': 6.75, 'I': 6.97, 'O': 7.51, 'A': 8.17, 'T': 9.06, 'E': 12.70}
for key, value in d.items():
    value = round(value * 100 / l, 2)
    r[key] = value
print(r)
